import * as React from "react"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Download } from "lucide-react"

export interface ExportButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  format?: "csv" | "pdf" | "json"
  onExport?: (format: string) => void
}

const ExportButton = React.forwardRef<HTMLButtonElement, ExportButtonProps>(
  ({ className, format = "csv", onExport, children, ...props }, ref) => {
    const handleClick = () => {
      onExport?.(format)
    }

    return (
      <Button
        ref={ref}
        className={cn("gap-2", className)}
        onClick={handleClick}
        {...props}
      >
        <Download className="h-4 w-4" />
        {children || `Export as ${format.toUpperCase()}`}
      </Button>
    )
  }
)
ExportButton.displayName = "ExportButton"

export { ExportButton }
