import * as React from "react"
import { cn } from "@/lib/utils"

export interface ReceiptCardProps extends React.HTMLAttributes<HTMLDivElement> {
  merchant: string
  amount: string
  date: string
  category?: string
}

const ReceiptCard = React.forwardRef<HTMLDivElement, ReceiptCardProps>(
  ({ className, merchant, amount, date, category, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={cn(
          "rounded-lg border bg-card text-card-foreground shadow-sm",
          className
        )}
        {...props}
      >
        <div className="p-6">
          <div className="flex items-center justify-between mb-2">
            <h3 className="text-lg font-semibold">{merchant}</h3>
            <span className="text-lg font-bold">{amount}</span>
          </div>
          <div className="flex items-center justify-between text-sm text-muted-foreground">
            <span>{date}</span>
            {category && (
              <span className="px-2 py-1 rounded-md bg-secondary">
                {category}
              </span>
            )}
          </div>
        </div>
      </div>
    )
  }
)
ReceiptCard.displayName = "ReceiptCard"

export { ReceiptCard }
